    private void btnIngresarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnIngresarActionPerformed
              
        if(txtUIT.getText().isEmpty()|| txtDolar.getText().isEmpty())
          {JOptionPane.showMessageDialog(this,"no dejar campos vac√≠os");}
              
        //valores que se ingresan por el textField del formulario
        Double uit=Double.parseDouble(txtUIT.getText());
        Double dollar=Double.parseDouble(txtDolar.getText());
              
        Double actUit=uit;
        Double actDollar=dollar;
        txtRes.setText("Los valores actuales son: ");
        txtRes.append("\nUIT: "+actUit);
        txtRes.append("\nDolar: "+actDollar);
        txtRes.append("\nLos valores anteriores son: ");
        txtRes.append("\nUIT: "+Menu_Principal.unidadImpositivaTributaria);
        txtRes.append("\nDolar: "+Menu_Principal.precioDolar);
        Menu_Principal.unidadImpositivaTributaria=actUit;
        Menu_Principal.precioDolar=actDollar;
                           
    }//GEN-LAST:event_btnIngresarActionPerformed

